CREATE OR REPLACE FUNCTION get_provision_group_id(p_provision_group INTEGER)
RETURNS INTEGER AS $$
DECLARE
  pg_id INTEGER;
BEGIN
  SELECT id INTO pg_id FROM nfr_provision_group WHERE provision_group = p_provision_group;
  RETURN pg_id;
END;
$$ LANGUAGE plpgsql;

